import io.micronaut.security.authentication.*;
import io.reactivex.Flowable;
import org.reactivestreams.Publisher;

import javax.inject.Inject;
import javax.inject.Singleton;
import java.util.Arrays;
import java.util.stream.Collectors;

@Singleton
public class AuthenticationProviderUserPassword implements AuthenticationProvider  {

    // change user repository with your service which retieives the user info
    @Inject
    private UserRepository userRepository;

    @Override
    public Publisher<AuthenticationResponse> authenticate(AuthenticationRequest authenticationRequest) {
        // replace this code with your code which retrieves the user information. 
       User user =  userRepository.findUserByUsernameAndPassword(
                authenticationRequest.getIdentity().toString(),
                authenticationRequest.getSecret().toString());
        //

        if ( user !=null ) {
            return Flowable.just(new UserDetails(user.getUsername(), user.getRoles()));
        }
        return Flowable.just(new AuthenticationFailed());
    }
}
